package service;import com.google.gson.FieldNamingPolicy;import com.google.gson.Gson;import com.google.gson.GsonBuilder;import modelo.conversionDTO;import java.io.FileWriter;import java.io.IOException;import java.util.List;public class convertirGson {    private final Gson gson;    public convertirGson() {        this.gson = new GsonBuilder()                .setFieldNamingPolicy(FieldNamingPolicy.LOWER_CASE_WITH_UNDERSCORES)                .setPrettyPrinting()                .create();    }    public conversionDTO convertirJson(String json) {        return gson.fromJson(json, conversionDTO.class);    }    public String convertirListas(List<conversionDTO> conversionDTOList) {        return gson.toJson(conversionDTOList);    }    public void gaurdarArchivoJson(Object contenido, String nombreArchivo) {        try (FileWriter writer = new FileWriter(nombreArchivo)) {            gson.toJson(contenido, writer);            System.out.println("\n Informaci√≥n guardada en: " + nombreArchivo);        } catch (IOException e) {            System.out.println("\n Error al guardar el archivo JSON " + e.getMessage());        }    }}